import React, { Component } from 'react';

export default class Messages extends React.Component {

    render(){
        const { messages } = this.props;
        console.log(messages);
        const messageList = messages.map(message => {
            if (this.props.moveMessages){
                return <li><marquee behaviour="alternate" onMouseOver="this.stop();" onMouseOut="this.start();" scrollAmount="16" onClick={() => {window.open("//" + "google.com/search?q=" + message.toString(), "_blank")}} color={() => '#' + Math.floor(Math.random() * 16777215).toString(16)}> {message} </marquee></li>;
            } else {
                return <li onClick={() => {window.open("//" + "google.com/search?q=" + message.toString(), '_blank');}}  color={() => '#' + Math.floor(Math.random() * 16777215).toString(16)}>{message}</li>;
            }

        });
        
        return messageList;
    }

}
