import React, { Component } from 'react';

export default class Messages extends React.Component {

    render(){
        const { messages } = this.props;
        console.log(messages);
        const messageList = messages.map(m => {
            let i = 0;
            if (this.props.moveMessages){
                i++;
                return <li key={m}><marquee behaviour="alternate" onMouseOver={this.stop} onMouseOut={this.start} onClick={() => {window.open("//" + "google.com/search?q=" + m.toString(), "_blank")}}> {m} </marquee></li>;
            } else {
                i++;
                return <li key ={m} onClick={() => {window.open("//" + "google.com/search?q=" + m.toString(), '_blank');}}> {m}</li>;
            }

        });
        
        return messageList;

        function randomColor() {
            return '#' + Math.floor(Math.random() * 16777215).toString(16);
        }
    }
}
