import React, {Component} from 'react';
import MessageForm from './MessageForm';
import MessageList from './MessageList';

let NUMBER_OF_MESSAGES = 10;

let scrollingMessages=true;

export default class MessagesPage extends React.Component{

    constructor(){
        super();
        this.state = { 
            numberOfMessagesDisplayed: 10,
            scrollingMessages: true  };
        this._collapseMessages = this._collapseMessages.bind(this);
        this._expandMessages = this._expandMessages.bind(this);
        this._toggleScroll = this._toggleScroll.bind(this);
    }

    _collapseMessages(){
        this.setState({numberOfMessagesDisplayed: 10});
    }

    _expandMessages(){
        this.setState({numberOfMessagesDisplayed: 100});
    }

    _toggleScroll(){
        let scroll = !this.state.scrollingMessages;
        this.setState({scrollingMessages: {scroll}});
    }

    messageControls(){
       return (<div>
        <button id="stop_movement" class="button_stuff" onclick="toggleScroll"> Stop Scroll
      </button>
      <button id="expand_button" class="button_stuff" onclick="expandMessages"> Expand
      </button>
      <button id="collapse_button" class="button_stuff" onclick="collapseMessages"> Collapse
      </button>
        </div>);
    }

    render(){
        return (<div>
            <MessageForm/>
            <hr></hr>
            <MessageList numberOfMessages={this.state.numberOfMessagesDisplayed} scrollingMessages={this.state.scrollingMessages}/>
        </div>);
    }


}
