import { combineReducers } from 'redux';

const defaultMessages = ["Most recently added message",
"This is a default message.",
"Is this twitter?",
"Message 2", "Hello!",
"Is Pluto a planet again?",
"Yes",
"@#()!",
"12345",
"Oldest message"];

const messagesReducer = (messages= [], action) => {
    if (action.type === 'CLEAR_MESSAGES'){
        return [];
    } else if (action.type === 'ADD_MESSAGE'){
        return messages.unshift(action.payload);
    }
    return messages;
}

export default combineReducers({
    messages: messagesReducer
    // more reducers here, need to add all of them
})
