import React, { Component } from 'react';
import { connect } from 'react-redux';

const startingSize = 100;

class MessageList extends React.Component {
   
   
    constructor(){
        super();
    }

    render() {
        console.log(this.props);
        let ul = document.createAttribute("ul");
        let messages = this.props.messages;
        let messagesLength = messages.length;
        let startingSize = determineStartingSize(messagesLength);
        let htmlMessages =[]
        for (var i = 0; i < this.props.numberOfMessagesDisplayed; i++) {
            let li = document.createElement("li");
            li.innerHTML = createInnerLIHTML(i, this.props.scrollingMessages, messages);
            formatMessageItem(li);
            startingSize = startingSize - 4;
            htmlMessages.append(li);
        }
        ul.innerHtml = htmlMessages;

        return (ul);
    }
}

function determineStartingSize(messagesLength) {
    if (messagesLength > 100) {
        return 300;
    }
    return 80;
}

function createInnerLIHTML(i, scrollingMessages, messages) {
    if (scrollingMessages === true) {
        return (<marquee behaviour="alternate" onMouseOver="this.stop();" onMouseOut="this.start();" scrollAmount="16" onClick={() => {window.open("//" + "google.com/search?q=" + messages[i], "_blank")}}> messages[i] </marquee>);
    } else {
        return (<li onClick={() => {window.open("//" + "google.com/search?q=" + messages[i], '_blank');}}>messages[i]</li>);
    }
}

function formatMessageItem(li) {
    li.style.color = '#' + Math.floor(Math.random() * 16777215).toString(16);
    li.style.fontSize = startingSize + "px";
    li.style.padding = "0px";
}

const mapStateToProps = (state) => {
    return {
        messages: state.messages
    }
}

export default connect(mapStateToProps)(MessageList);
