import React, {Component} from 'react';

let messages = [];

let MAX_LIST_LENGTH = 10000;

let messagesToDisplay = 10;

let displayDefaults = true;

let moveMessages = true;

let storedMessages = JSON.stringify({
    "messages": ["Most recently added message",
        "This is a default message.",
        "Is this twitter?",
        "Message 2", 
        "Hello!",
        "Is Pluto a planet again?",
        "Yes",
        "@#()!",
        "12345",
        "Oldest message"]
});

export default class Messages extends React.Component{
    
    render(){
        return (<div><MessageForm/>
            <MessageList/></div>);
    }
}


class MessageForm extends React.Component{
    render(){
        return (<div>
      <form id="form" onsubmit="addText(this.text.value); return false;">
        <label>
          <input id="input_field" name="text" type="text" value="Insert Message Here..."
            onfocus="if (this.value == 'Insert Message Here...') {this.value = '';}" size="36" />

        </label>
        <br />
        <input id="add" type="submit" value="Submit" />
        <input type="reset" class="button_stuff" value="Clear Form" />
        <button id="clear_button" class="button_stuff" onclick="clearTextLog(); return false;">Clear Message Log
        </button>
      </form>
    </div>);
    }

    addText(textInput) {
        document.getElementById("form").reset();
    
        if (textInput !== "Insert Message Here...") {
            messages.unshift(textInput);
            clearUL();
            displayMessages();
        }
    }

}
