import React, { Component } from 'react';

export default class Messages extends React.Component {

    render(){
        const { messages } = this.props;
        console.log(messages);
        let messageList = [];
        console.log(this.props.numberOfMessages);
        for (var i = 0; i < this.props.numberOfMessages; i++){
            let message = messages[i];
            if (this.props.moveMessages){
                messageList.push(<li key={i}><marquee behaviour="alternate" onMouseOver={this.stop} onMouseOut={this.start} onClick={() => {window.open("//" + "google.com/search?q=" + message.toString(), "_blank")}}> {message} </marquee></li>);
            } else {
                messageList.push(<li key ={i} onClick={() => {window.open("//" + "google.com/search?q=" + message.toString(), '_blank');}}> {message} </li>);
            }
        }
        
        return messageList;

        function randomColor() {
            return '#' + Math.floor(Math.random() * 16777215).toString(16);
        }
    }
}
