import React, { Component } from 'react';

export default class Messages extends React.Component {

    render(){
        const { messages } = this.props;
        console.log(messages);
        const messageList = [];
        for (var i = 0; i < messages.length; i++){
            if (this.props.moveMessages){
                messageList.append(<li key={i}><marquee behaviour="alternate" onMouseOver={this.stop} onMouseOut={this.start} onClick={() => {window.open("//" + "google.com/search?q=" + messages[i].toString(), "_blank")}}> {messages[i]} </marquee></li>);
            } else {
                messageList.append(<li key ={i} onClick={() => {window.open("//" + "google.com/search?q=" + messages[i].toString(), '_blank');}}> {messages[i]}</li>);
            }
        }
        
        return messageList;

        function randomColor() {
            return '#' + Math.floor(Math.random() * 16777215).toString(16);
        }
    }
}
