import React, { Component } from 'react';

export default class Messages extends React.Component {

    render(){
        const { messages } = this.props;
        const messageList = messages.map(message => {
            if (this.props.moveMessage){
                (<li><marquee behaviour="alternate" onMouseOver="this.stop();" onMouseOut="this.start();" scrollAmount="16" onClick={() => {window.open("//" + "google.com/search?q=" + {message}, "_blank")}} color={() => '#' + Math.floor(Math.random() * 16777215).toString(16)}> {message} </marquee></li>);
            } else {
                (<li onClick={() => {window.open("//" + "google.com/search?q=" + {message}, '_blank');}}  color={() => '#' + Math.floor(Math.random() * 16777215).toString(16)}>{message}</li>);
            }

        });
        
        return messageList;
    }

}
