import React from 'react';
import MessageList from './MessageList';

export default class MessagesPage extends React.Component {

    constructor() {
        super();
        this.state = {
            numberOfMessagesDisplayed: 10,
            scrollingMessages: true
        };
        this._collapseMessages = this._collapseMessages.bind(this);
        this._expandMessages = this._expandMessages.bind(this);
        this._toggleScroll = this._toggleScroll.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(event) {
        event.preventDefault();
        let newMessage = event.target.text.value.toString();
        this.sendMessageWithPOST(newMessage);
    }

    clearMessages() {
        const options = {
            method: 'POST'
        };

        fetch("http://localhost:9000/messages/clearAll", options);
    }

    sendMessageWithPOST(newMessage) {
        const options = {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ "message": newMessage })
        };

        fetch("http://localhost:9000/messages", options);
        this.forceUpdate();
    }

    _collapseMessages() {
        this.setState({ numberOfMessagesDisplayed: 10 });
    }

    _expandMessages() {
        this.setState({ numberOfMessagesDisplayed: 50 });
    }

    _toggleScroll() {
        if (this.state.scrollingMessages){
        this.setState({ scrollingMessages: false });
        } else {
            this.setState({ scrollingMessages: true });
        }
    }

    render() {
        let scrollButton;

        if (this.state.scrollingMessages){
            scrollButton = (<button id="stop_movement" className="btn btn-secondary btn-sm" onClick={this._toggleScroll}> Stop Scroll
            </button>);
        } else {
            scrollButton = (<button id="stop_movement" className="btn btn-secondary btn-sm" onClick={this._toggleScroll}> Resume Scroll
            </button>);
        }

        let expandCollapseButton;
        if (this.state.numberOfMessagesDisplayed === 10){
            expandCollapseButton = (<button id="expand_button" className="btn btn-secondary btn-sm" onClick={this._expandMessages}> Expand</button>);
        } else {
            expandCollapseButton = (<button id="collapse_button" className="btn btn-secondary btn-sm" onClick={this._collapseMessages}> Collapse</button>);
        }
        
        return (<div>
            
            <div>
                <form className=".form-horizontal" onSubmit={this.handleSubmit}>
                    <label className="input_label">

                        <input className="input_field" ref={input => this.inputField = input} name="text" type="text" defaultValue="Insert Message Here..." onFocus={() => this.inputField.value = ""} />
                    </label>
                    <br />
                    <input className="btn btn-secondary btn-sm btn-block" type="submit" value="Submit" />
                    <input type="reset" className="btn btn-secondary btn-sm btn-block" value="Clear Form" />
                </form>
                <div className="clear_button">
                    <button className="btn btn-secondary btn-sm btn-block" onClick={() => this.clearMessages()}>Clear Message Log
        </button>
                </div>
            </div>
            <hr></hr>
            <MessageList numberOfMessages={this.state.numberOfMessagesDisplayed} scrollingMessages={this.state.scrollingMessages} />
            <div>
                {scrollButton}
                {expandCollapseButton}
            </div>
        </div>);
    }

}
