import { combineReducers } from 'redux';

let defaults = [];

const messagesReducer = (messages= defaults, action) => {
  if (action.type === "ADD_MESSAGE"){
        return defaults.unshift(action.payload);
        
    }
    return messages;
    
};


export default combineReducers({
    messages: messagesReducer,
})
