import React from 'react';

export default class Messages extends React.Component {

    constructor(){
        super();
        this.state = {
            showPopUp: false
        };
    }

    render(){
        
        console.log(this.props.messages);
        console.log("hello");
        const { messages } = this.props;
        let messageList = [];
        for (var i = 1; i < this.props.numberOfMessages; i++){
            let message = messages[i].message;
            let link = messages[i].link;
            if (this.props.moveMessages){
                messageList.push(<li key={i}><marquee behaviour="alternate" onClick={() => {window.open(link, 'popup', 'width=400,height=400' ); return false;}} > {message} </marquee></li>);
            } else {
                messageList.push(<li key ={i} onClick={() => {window.open(link, 'popup', 'width=400,height=400' ); return false;}}> {message} </li>);
            }
        }
        
        return messageList;

    }
}
