import React, { Component } from 'react';

export default class Messages extends React.Component {

    render(){
        const { messages } = this.props;
        console.log(messages);
        const messageList = messages.map((message, i) => {
            if (this.props.moveMessages){
                return <li key={i}><marquee behaviour="alternate" onMouseOver={this.stop} onMouseOut={this.start} scrollAmount="16" onClick={() => {window.open("//" + "google.com/search?q=" + message.toString(), "_blank")}} color={randomColor}> {message} </marquee></li>;
            } else {
                return <li key ={i} onClick={() => {window.open("//" + "google.com/search?q=" + message.toString(), '_blank');}}  color={randomColor.toString()}> {message}</li>;
            }

        });
        
        return messageList;

        function randomColor() {
            return '#' + Math.floor(Math.random() * 16777215).toString(16);
        }
    }
}
