import React, {Component} from 'react';
import {connect} from 'react-redux';
import { clearMessages, addMessage } from '../actions';

class MessageForm extends React.Component{
    constructor(){
        super();
        this._clearMessages = this._clearMessages.bind(this);
        this._addMessage = this._addMessage.bind(this);
    }

    _addMessage = (message) => {
        this.props.addMessage(message);
    }

    _clearMessages = () => {
        this.props.clearMessages();
    }

    render(){
        return (
            <div>
      <form id="form" onSubmit={() => this._addMessage(this.text.value)}>
        <label>
          <input id="input_field" ref={input => this.inputField=input} name="text" type="text" defaultValue="Insert Message Here..." onFocus = {() => this.inputField.value=""}/>
        </label>
        <br />
        <input id="add" type="submit" value="Submit" />
        <input type="reset" className="button_stuff" value="Clear Form" />
        <button id="clear_button" className="button_stuff" onClick={this._clearMessages}>Clear Message Log
        </button>
      </form>
    </div>
        );
    }

}

const mapStateToProps = (state) => {
    return {
        messages: state.messages
    }
}

const mapActionsToProps = {

};

export default connect(mapStateToProps, mapActionsToProps)(MessageForm);
