import React, {Component} from 'react';

export default class MessageForm extends React.Component{
    constructor(){
        super();

    }

    _onFocus = () => {
        if (this.value == 'Insert Message Here...') {this.value = '';}
    }


    render(){
        return (
            <div>
      <form id="form" onSubmit="">
        <label>
          <input id="input_field" name="text" type="text"  />

        </label>
        <br />
        <input id="add" type="submit" value="Submit" />
        <input type="reset" className="button_stuff" value="Clear Form" />
        <button id="clear_button" className="button_stuff" onClick="">Clear Message Log
        </button>
      </form>
    </div>
        );
    }

}
