import React, {Component} from 'react';
import {connect} from 'react-redux';

class MessageList extends React.Component{
    constructor(){
        super();
    }

    render(){
        console.log(this.props);
        let ul = document.createAttribute("ul");
        let startingSize = determineStartingSize();
        for (var i = 0; i < this.props.messagesLength; i++){
                let li = document.createElement("li");
                li.innerHTML = createInnerLIHTML();
                formatMessageItem(li);
                startingSize = startingSize - 4;
                ul.appendChild(li);
        }

        return ({ul});

        function determineStartingSize(){
            if (this.props.messagesLength > 100){
                return 300;
            }
            return 80;
        }

        function createInnerLIHTML() {
            if (this.props.scrollingMessages === true) {
                return "<marquee behaviour=\"alternate\" onmouseover=\"this.stop();\" onmouseout=\"this.start();\" scrollamount=16>" + this.props.messages[i] + "</marquee>";
            } else {
                return this.props.messages[i];
            }
        }

        function formatMessageItem(li) {
            li.style.color = '#' + Math.floor(Math.random() * 16777215).toString(16);
            li.style.fontSize = startingSize + "px";
            li.style.padding = "0px";
        }
    }
}

const mapStateToProps = (state) => {
    return {
        messages: state.messages,
        messagesLength: state.messages.length
    }
}

export default connect(mapStateToProps, {})(MessageList);
