import React, {Component} from 'react';

export default class MessageForm extends React.Component{
    constructor(){
        super();
        this.textInput = React.createRef();

    }

    _focus = () => {
        if (this.textInput.value === "Insert Message Here..."){
            this.textInput.value = '';
        } 
    }


    render(){
        return (
            <div>
      <form id="form" onSubmit="">
        <label>
          <input id="input_field" ref ={this.textInput} name="text" type="text" defaultValue="Insert Message Here..." onClick={this._focus} />

        </label>
        <br />
        <input id="add" type="submit" value="Submit" />
        <input type="reset" className="button_stuff" value="Clear Form" />
        <button id="clear_button" className="button_stuff" onClick="">Clear Message Log
        </button>
      </form>
    </div>
        );
    }

}
