import React, { Component } from 'react';
import {connect} from 'react-redux';
import { createIFrame } from '../actions';

class Messages extends React.Component {

    constructor(){
        super();
        this.state = {
            showPopUp: false
        };
    }

    createAndStoreIFrame(message){
        let url =("//" + "google.com/search?q=" + message.toString(), '_blank');

        this.props.createIFrame(message.toString());
        
    }

    render(){
        const { messages } = this.props;
        console.log(messages);
        let messageList = [];
        console.log(this.props.numberOfMessages);
        for (var i = 0; i < this.props.numberOfMessages; i++){
            let message = messages[i];
            if (this.props.moveMessages){
                messageList.push(<li key={i}><marquee behaviour="alternate" onMouseOver={this.stop} onMouseOut={this.start} onClick={this.createAndStoreIFrame(message)}> {message} </marquee></li>);
            } else {
                messageList.push(<li key ={i} onClick={ this.createAndStoreIFrame(message)}> {message} </li>);
            }
        }
        
        return messageList;

    }
}

const mapStateToProps = (state) => {
    return {
        link: state.link
    }
}

export default connect(mapStateToProps, {createIFrame})(Messages);
