import React, { Component } from 'react';


export class AboutText extends React.Component {

    render() {
        const aboutTitle = <h2>About This Page</h2>;
        const aboutText = (
            <div>
                <p>These webpages were created by Martin Palanca, a 4th Year BCS Computer Science student, as part of Assignment 1 for the <i>CPSC436I (Introduction to Industry Skills)</i>
                     course at the University of British Columbia. The primary functionality of this site allows users to: </p>
                <ul>
                    <li>
                        <b>Add custom messages into a Message Log </b>. Messages are ordered from <i>top to bottom</i> from <i>newest
            to oldest</i>. Only the <i>ten</i> most recently entered messages will be viewable on the default view. All
          messages can be viewed using the "Expand" buttom at the bottom of the log. </li>
                    <li>
                        <b> Clear the existing Message Log.</b>
                        Users can also clear the existing Message Log using the "Clear Message Log" button.
                    </li>
                    <li>
                        <b> Clear the input form.</b>
                        Users can clear existing input from the form field with the "Clear Form" button.
                    </li>
                    <li>
                        <b> Start/Stop scrolling of messages.</b>
                        The default behaviour of the Message Log is to scroll messages from right to left. Message scrolling can be
                        stopped with the "Stop Scroll" button, and resumed again with the "Start Scroll" button.
        </li>
                </ul>
            </div>);

        return (<div>
            {aboutTitle}
            {aboutText}
                </div>
        )
    }

}

export default AboutText;
