import React, {Component} from 'react';

export default class MessageForm extends React.Component{
    render(){
        return (
            <div>
      <form id="form" onsubmit="">
        <label>
          <input id="input_field" name="text" type="text" value="Insert Message Here..."
            onfocus="if (this.value == 'Insert Message Here...') {this.value = '';}" size="36" />

        </label>
        <br />
        <input id="add" type="submit" value="Submit" />
        <input type="reset" class="button_stuff" value="Clear Form" />
        <button id="clear_button" class="button_stuff" onClick="">Clear Message Log
        </button>
      </form>
    </div>
        );
    }

}
