import React, {Component} from 'react';


export default class Messages extends React.Component{
    

    render(){
        return (<div><MessageForm/>
            <MessageList/></div>);
    }
}

class MessageList extends React.Component{

    messages = [];

    renderControls(){
       return  (<div id="log_controls">
      <button id="stop_movement" class="button_stuff" onclick="stopMovement();"> Stop Scroll
      </button>
      <button id="expand_button" class="button_stuff" onclick="expandMessages();"> Expand
      </button>
      <button id="collapse_button" class="button_stuff" onclick="collapseMessages();"> Collapse
      </button>
    </div>);
    }

    displayMessages() {
        if (messages.length == 0 && displayDefaults == true) {
            // Initializes default messages into messages if the page was newly loaded (messages is empty). 
            initializeDisplayedMessagesWithDefaults();
        }
    
        displayAvailableMessages();
    }

    initializeDisplayedMessagesWithDefaults() {
        let defaultMessages = JSON.parse(storedMessages);
        messages = messages.concat(defaultMessages.messages);
    }
    
    determineStartingSize(messagesToDisplay) {
        if (messages.length > 80) {
            return 300;
        } else {
            return 50;
        }
    }
    
    randomColour() {
        return '#' + Math.floor(Math.random() * 16777215).toString(16);
    }

    displayAvailableMessages() {
        let ul = document.createElement("ul");
        let startingSize = determineStartingSize(messagesToDisplay);
        for (var i = 0; i < messagesToDisplay; i++) {
            if (messages[i]) {
                let li = document.createElement("li");
                li.innerHTML = createInnerLIHTML();
                formatMessageItem(li);
                startingSize = startingSize - 4;
                ul.appendChild(li);
            }
        }
    
        function formatMessageItem(li) {
            li.style.color = randomColour();
            li.style.fontSize = startingSize + "px";
            li.style.padding = "0px";
        }
    
        function createInnerLIHTML() {
            if (moveMessages == true) {
                return "<marquee behaviour=\"alternate\" onmouseover=\"this.stop();\" onmouseout=\"this.start();\" scrollamount=16>" + messages[i] + "</marquee>";
            } else {
                return messages[i];
            }
        }

        return ul;
    }

    render(){

    }
}

class MessageForm extends React.Component{
    render(){
        return (<div>
      <form id="form" onsubmit="addText(this.text.value); return false;">
        <label>
          <input id="input_field" name="text" type="text" value="Insert Message Here..."
            onfocus="if (this.value == 'Insert Message Here...') {this.value = '';}" size="36" />

        </label>
        <br />
        <input id="add" type="submit" value="Submit" />
        <input type="reset" class="button_stuff" value="Clear Form" />
        <button id="clear_button" class="button_stuff" onclick="clearTextLog(); return false;">Clear Message Log
        </button>
      </form>
    </div>);
    }
}
