import React, { Component } from 'react';
import AboutText from './AboutText';
import MessagesPage from './MessagesPage';

export default class App extends React.Component {

    constructor() {
        super();
        if (!this.state) {
            this.state = { isHomePage: true };
        }
    }

    _updateHomePage = () => {
        this.setState({ isHomePage: false });
    }

    _updateAboutPage= () => {
        this.setState({ isHomePage: true });
    }

    render() {
        let customHeader = (
            <div>
                <div>
                    <h1 id="page_title">CPSC436I Message Logger</h1>
                    <hr></hr>
                    <link rel="stylesheet" type="text/css" href="index.css" />

                </div>
                <header>
                    <nav>
                        <span><button className="button-stuff" id="homeButtom" onClick={_updateHomePage}>HOME</button>|<button className="button-stuff" id="aboutButton" onClick={this._updateAboutPage}>ABOUT</button></span>
                    </nav>
                </header>
                <hr></hr>
            </div>
        );

        let body;

        if (this.state) {
            if (this.state.isHomePage === false) {
                body = (<div><AboutText /></div>);
            } else {
                body = (<div>
                    <MessagesPage />
                </div>);
            }
        }

        return (
            <div>
                <div>{customHeader}</div>
                <div>{body}</div>
            </div>
        );
    }

}
