import React, {Component} from 'react';
import MessageForm from './components/MessageForm.js';
import MessageList from './components/MessageList';

let NUMBER_OF_MESSAGES = 10;

let scrollingMessages=true;

export default class MessagesPage extends React.Component(){

    messageControls(){
       return (<div>
        <button id="stop_movement" class="button_stuff" onclick="toggleScroll"> Stop Scroll
      </button>
      <button id="expand_button" class="button_stuff" onclick="expandMessages"> Expand
      </button>
      <button id="collapse_button" class="button_stuff" onclick="collapseMessages"> Collapse
      </button>
        </div>);

        function collapseMessages(){
            NUMBER_OF_MESSAGES= 10;
        }

        function expandMessages(){
            NUMBER_OF_MESSAGES = 100;
        }

        function toggleScroll(){
            scrollingMessages = !scrollingMessages;
        }

    }

    render(){
        <div>
            <MessageForm/>
            <MessageList numberOfMessages = "{NUMBER_OF_MESSAGES}" scrollingMessages = "{scrollingMessages}"/>
        </div>
    }


}
